mapper.select:
select r_num,drug_img,drug_idx,drug_name,drug_formulation, drug_component,drug_company,drug_Professional_General ,drug_date from( select rownum r_num, a.*from
(select * from drug order by drug_name asc) a
(select d.*,m.drug_case_num from drug d, my_drug_case m where d.drug_idx in(select drug_idx from my_drug_case where num = #{loginNUM}) and d.drug_idx = m.drug_idx order by drug_case_num desc) a
(select d.*,m.drug_search_num,m.drug_date from drug d, drug_search_history m where d.drug_idx in(select drug_idx from drug_search_history where num = #{loginNUM}) and d.drug_idx = m.drug_idx order by drug_search_num desc)a
) where r_num between #{begin} and #{end}

select
r_num,drug_img,drug_idx,drug_name,drug_formulation,
drug_component,drug_company,drug_Professional_General
from(
select
rownum r_num, a.*from

(select * from drug where drug_color_F= #{color} and drug_shape =#{shape} and
drug_identification Like '%'||#{identification}||'%')a

) where r_num between #{begin} and #{end}
